function params = displayParams% For Scanner UMC 7T scanner 1024x768 resolution% 09/2005 SOD: used for front (feet) projector in 3T UMC  scanner.% 26/05/2009 BMH: Modified for UMC 7T scanner% 28/05/2009 BMH: Modified for UMC 7T scanner projector at 1024x768 resolution% Critical parametersparams.numPixels = [1024 768];%Old projector setupparams.dimensions = [10 7.5];%Normal%params.Rect = [243 230 781 768]; %params.Rect = [256 256 768 768];%Good for recording movies at 512x512 pixels [256 256 768 768];%params.Rect = [0 0 1024 768];%NORMAL USE THISparams.Rect = [128 0 896 768];params.Rect=params.Rect(:);%Fixed lens setup% params.dimensions =[9.5 7.125];% % % %NORMAL USE THIS% params.Rect = [128 0 896 768];% params.Rect=params.Rect(:);params.distance = [41]; %[28] %position 3,3    %[35.5]%position 2,3  %[41] %position 1,1 params.frameRate = 60;params.cmapDepth = 8;params.screenNumber = 1;params.flipLR=0;params.flipUD=0;% if params.flipLR ==1%     params.Rect(1)=-params.Rect(1);%     params.Rect(3)=-params.Rect(3);% end% if params.flipUD ==1%     params.Rect(2)=-params.Rect(2);%     params.Rect(4)=-params.Rect(4);% end%THIS PAIR OF LINES ALLOWS THE ENTIRE STIMULUS TO BE PUT INTO A RESTRICTED%RECTANGLE TO FIT INTO THE VIEWING AREA OF THE 7T PROJECTOR. THIS VIEWING%AREA IS WIDE, BUT SHORT, AND THE RESTRICTED AREA IS RESTRAINED TO A%SQUARE, REMOVING THE OPTION TO PROJECT A WIDE STIMULUS. IT IS SOMETIMES%BETTER TO MAKE A BIG STIMULUS, BUT MAKE TO TOP AND BOTTOM FALL OFF THE%VIEWING AREA. THIS IS EASILY DONE IN makeRetinotopyStimulus JUST AFTER THE%MESH GRID IS DEFINED, AROUND LINE 60 USING A LINE LIKE THIS:%y=y+pix2angle(params.display, 115);%THIS SHIFTS THE VIEWING AREA DOWN 115 PIXELS, ABOUT THE RIGHT AMOUNT.%REMEMBER THAT THE FIXATION POINT SHOULD PROBABLY BE MOVED TOO. THIS CAN BE%DONE IN setRetinotopyParams, JUST AFTER params.display.fixY IS SET, AROUND%LINE 600. FOR THE SAME OFFSET, I USED:%params.display.fixY=params.display.fixY+115;% Descriptive parametersparams.computerName = 'legolas';params.monitor = 'projector';params.card = 'ATI Mobility Radeon 9700';params.position = '7T UMC scanner rear-to-front';     