function params = displayParams% For Scanner UMC 7T scanner 1024x768 resolution% 09/2005 SOD: used for front (feet) projector in 3T UMC  scanner.% 26/05/2009 BMH: Modified for UMC 7T scanner% 28/05/2009 BMH: Modified for UMC 7T scanner projector at 1024x768 resolution% Critical parameters% params.numPixels = [1024 768];params.numPixels = [512 768]; % stereo viewing% params.dimensions = [15 11.25];params.dimensions = [7.5 11.25]; % stereo viewingparams.distance = [30];params.frameRate = 60;params.cmapDepth = 8;params.screenNumber = 1;% params.viewableRect = [0 0 512 538]; % stereo viewingparams.viewableRect = [0 230 512 768]; % stereo viewing%params.Rect=params.viewableRect;% params.rect=params.rect(:);if exist('screenRotation.mat','file')    load('screenRotation.mat');    params.screenRotation = screenRotation;else    disp('No previous screen rotation value found');    params.screenRotation=0;endparams.stereoMode = 4; % Stereo for cross-fusingif params.stereoMode > 0    params.stereoFlag = 1;else    params.stereoFlag = 0;endparams.flipLR=0;params.flipUD=0;% Descriptive parametersparams.computerName = 'legolas';params.monitor = 'projector';params.card = 'ATI Mobility Radeon 9700';params.position = '7T UMC scanner rear-to-front';     