function params = displayParams% For Utrecht Stereoscope 2048x768 resolution% Critical parameters% params.numPixels = [1024 768];params.numPixels = [1024 768]; % stereo viewing% params.dimensions = [15 11.25];params.dimensions = [36 27];%[7.5 11.25]; % stereo viewing%params.dimensions = [12.5 18.8]; % stereo viewingparams.distance = 75;%[30];params.frameRate = 85;%60;params.cmapDepth = 8;params.screenNumber = 1;% params.viewableRect = [0 0 512 538]; % stereo viewingparams.viewableRect = [0 0 1024 768];%[0 0 512 538];%[0 0 512 538]; % stereo viewing% params.rect=params.rect(:);if exist('screenRotation.mat','file')    disp('Loading previous screen rotation');    load('screenRotation.mat');    params.screenRotation = screenRotation;    params.horizontalOffset = horizontalOffset;else    disp('No previous screen rotation value found');    params.screenRotation=0;    params.horizontalOffset = 0;end% Descriptive parametersparams.computerName = 'legolas';params.monitor = 'projector';params.card = 'ATI Mobility Radeon 9700';params.position = '7T UMC scanner rear-to-front';     